'modal-help_ponderation':'<div class="modal fade" data-scrutari-modal="help_ponderation" tabindex="-1" role="dialog" aria-hidden="true"> <div class="modal-dialog"> <div class="modal-content"> <div class="modal-header"> <p class="modal-title">_ ponderation_help</p> </div> <div class="modal-body">_ help_ponderation.html</div> <div class="modal-footer"> <button type="button" class="btn btn-default" data-dismiss="modal">_ button_close</button> </div> </div> </div> </div>',
